package basics;

public class Schleife {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 10; i++) {
			System.out.println("i = " + i);
		}
		System.out.println("");
		for (int j = 1; j <= 10; j++) {
			System.out.println("j = " + j);
		}
		System.out.println("fertig");
	}

}
